snippets.breadcrumb:
{{ govukBreadcrumbs({
	items: [
		{
			text:'Home',
		href: '/'
	},
		{
			text:'${1:Section name}',
		href: '${2:#0}'
	},
		{
			text:'${3:Current page}'
	}
	]
}) }}

snippets.button:
{{ govukButton({
	text: '${1:Save and continue}',
	href: '${2:#0}',
	classes: '$3'
}) }}

snippets.checkboxes:
{{ govukCheckboxes({
	idPrefix: '$1',
	name: '$2',
	fieldset: {
		legend: {
			text: '${3:Question text}',
			isPageHeading: ${4:true},
			classes: '${5:govuk-fieldset__legend--xl}'
		}
	},
	hint: {
		text: '${6:Hint text}'
	},
	items: [
		{
			value: '$7',
			text: '$8',
			conditional: {
				html: ${9:Conditional name}
			}
		},
		{
			value: '$10',
			text: '$11',
			conditional: {
				html: ${12:Conditional name}
			}
		}
	]
}) }}

snippets.date:
{{ govukDateInput({
	id: '${1:dob}',
	name: '${2:dob}',
	fieldset: {
		legend: {
			text: '${3:What is your date of birth?}',
			isPageHeading: ${4:true},
			classes: '${5:govuk-fieldset__legend--xl}'
		}
	},
	hint: {
		text: '${6:For example, 31 3 1980}'
	},
	items: [
		{
			name: '${7:day}'
		},
		{
			name: '${8:month}'
		},
		{
			name: '${9:year}'
		}
	]
}) }}

snippets.details:
{{ govukDetails({
	summaryText: '${1:Help with nationality}',
	text: '${2:We need to know your nationality so we can work out which elections you’re entitled to vote in. If you can’t provide your nationality, you’ll have to send copies of identity documents through the post.}'
}) }}

snippets.upload:
{{ govukFileUpload({
	id: '${1:file-upload-1}',
	sname: '${2:file-upload-1}',
	label: {
		text: '${3:Upload a file}'
	}
}) }}

snippets.inset:
{% from 'inset-text/macro.njk' import govukInsetText %}

{{ govukInsetText({
	${1:text or html}: '$2',
	classes: '$3'
}) }}

snippets.panel:
{{ govukPanel({
	titleText: '${1:Application complete}',
	text: '${2:Your reference number: HDJ2123F}',
	classes: '$3'
}) }}

snippets.radios:
{{ govukRadios({
	classes: '${1:govuk-radios--inline}',
	idPrefix: '$2',
	name: '$3',
	fieldset: {
		text: '$4',
		isPageHeading: ${5:true},
		classes: '${6:govuk-fieldset__legend--xl}'
	},
	hint: {
		text: '$7'
	},
	items: [
		{
			value: '$8',
			text: '$9'
		},
		{
			value: '$10',
			text: '$11'
		}
	]
}) }}

snippets.select:
{{ govukSelect({
	classes: '$1',
	id: '$2',
	name: '$3',
	label: {
		html: '$4',
		isPageHeading: ${5:true}
	},
	hint: {
		text: '$6'
	},
	items: [
		{
			value: '$7',
			text: '$8'
		},
		{
			value: '$9',
			text: '$10'
		}
	]
}) }}

snippets.table:
{{ govukTable({
	classes: '$1',
	caption: '$2',
	firstCellIsHeader: '${3:true}',
	head: [
		{
			text: '$4'
		},
		{
			text: '$5',
			${6:format: 'numeric'}
		},
		{
			text: '$7',
			${8:format: 'numeric'}
		}
	],
	rows: [
		[
			{
				text: '$9'
			},
			{
				text: '$10',
				${11:format: 'numeric'}
			},
			{
				text: '$12',
				${13:format: 'numeric'}
			}
		]
	]
}) }}

snippets.tabs:
{% from 'tabs/macro.njk' import govukTabs %}

{% set ${1:Tab 1 HTML name} %}
	Tab content
{% endset -%}

{% set ${2:Tab 2 HTML name} %}
	Tab content
{% endset -%}

{{ govukTabs({
	classes: '$3',
	items: [
		{
			label: '$4',
			id: '$5',
			panel: {
				html: $1
			}
		},
		{
			label: '$6',
			id: '$7',
			panel: {
				html: $2
			}
		}
	]
}) }}

snippets.input:
{{ govukInput({
	label: {
		text: '${1:Label text}',
		isPageHeading: ${2:true}
	},
	hint: {
		text: '${3:Hint text}'
	},
	id: '$4',
	name: '$5',
	classes: '${6:govuk-input--width-10}'
}) }}

snippets.textarea:
{{ govukTextarea({
	classes: '$1',
	name: '$2',
	id: '$3',
	rows: '$4',
	label: {
		text: '$5'
	},
	hint: {
		text: '$6'
	}
}) }}
